create database datagovdb;

use datagovdb;
CREATE LOGIN [bbfpdopv] FROM EXTERNAL PROVIDER;

use datagovdb;
CREATE USER [bbfpdopv] FOR LOGIN [bbfpdopv];
ALTER ROLE db_datareader ADD MEMBER [bbfpdopv];

GRANT REFERENCES ON DATABASE SCOPED CREDENTIAL::[scoped_credential] TO [bbfpdopv];

--In the master database
CREATE LOGIN [amlsvruser] FROM EXTERNAL PROVIDER;

-- In your database
CREATE USER amlsvruser FROM LOGIN [amlsvruser];

ALTER ROLE db_owner ADD member alias; -- Type USER name from step 2

CREATE LOGIN [amlsvruser] FROM EXTERNAL PROVIDER;
ALTER SERVER ROLE sysadmin ADD MEMBER [amlsvruser];


CREATE LOGIN sqluser WITH PASSWORD = 'AzureSQL!@#$';

